int main(){
    printf("\033[?25l"); // Hides the cursor highlighting
    system("cls");
    if (level_no==1){
    printf("\t___________.__                _____                        \n"
"\t\\__    ___/|  |__   ____     /     \\ _____  ________ ____  \n"
"\t  |    |   |  |  \\_/ __ \\   /  \\ /  \\__  \\ \\___   // __ \\ \n"
"\t  |    |   |   Y  \\  ___/  /    Y    \\/ __ \\_/    /\\  ___/ \n"
"\t  |____|   |___|  /\\___  > \\____|__  (____  /_____ \\___  >\n"
"\t                \\/     \\/          \\/     \\/      \\/    \\/ \n\n\n");
        printf("\t\tINSTRUCTIONS\n=> Move the character using arrow keys or WASD.\n=> Reach the goal (represented by !)\n=> Make sure the entire game screen is visible on the terminal, do not resize the window.\n\nPress any key to continue...");
        getch();
    }
    system("cls");
    char maze[20][20];
    new_level(maze,level_no);
    char key, sprite='>'; bool end=false;
    int cx=0, cy=0, px=0, py=0;
    for (int i=0;i<20;i++){
        for (int j=0;j<20;j++) {
            if (maze[i][j]!='#' && maze[i][j]!='!') maze[i][j]=' '; 
            printf("%2c",maze[i][j]); 
        }
        printf("\n");
    }

    printf("\n\t\tLEVEL %d",level_no);
    while (true){
        printf("\033[%d;%dH%c",1+cy,2+2*cx,sprite); // Using the x and y coordinates, draws the player onto the screen
        px=cx; py=cy;
        if (end) break;
        key=getch();
        if (key=='q') break;
        else if (cy!=0 && maze[cy-1][cx]!='#' && (key=='w' || key==72)) {cy--; sprite='^'; moves++;}
        else if (cy!=19 && maze[cy+1][cx]!='#' && (key=='s' || key==80)) {cy++; sprite='v'; moves++;}
        else if (cx!=0 && maze[cy][cx-1]!='#' && (key=='a' || key==75)) {cx--; sprite='<'; moves++;}
        else if (cx!=19 && maze[cy][cx+1]!='#' && (key=='d' || key==77)) {cx++; sprite='>'; moves++;}
        if (cx!=px || cy!=py) printf("\033[%d;%dH ",1+py,2+2*px); // Removes the previous drawing of the character

        if (maze[cy][cx]=='!') {
            new_level(maze,++level_no);
            main();
        }
    }
}